function cowSay(text = 'Enter text!') {
  if (!text) {
    text = 'Enter text!'
  }
  const output = []
  for (let i = 0; i < text.length + 2; i++) {
    output.push('-')
  }
  output.push('\n' + ' ' + text + '\n')
  for (let i = 0; i < text.length + 2; i++) {
    output.push('-')
  }
  output.push('\n')
  output.push(`
    \\   ^__^
     \\  (oo)\\_______
        (__)\\       )\\/\\
            ||----w |
            ||     ||
  `)
  return output.join('')
}

module.exports = cowSay